
public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//while loop
		int a=1;
		while (a<10)
		{
			System.out.println(a);
		     a+=2;
		}
		System.out.println("exited");
	
	
	//do while loop
	
	int b=2 ;
	
	do
		
	{System.out.println(b);
	b+=2;
		
	}
	while(b<=10);
	
}
}
